// Enhanced Input Adapter with comprehensive configuration
const fromTrigger = $input.first()?.json || {};
const config = {
  // Alert channels and formatting
  SLACK_CHANNEL: fromTrigger.SLACK_CHANNEL || "#finance-alerts",
  EMAIL_ALERTS: fromTrigger.EMAIL_ALERTS ?? false,
  POST_SUMMARY: fromTrigger.POST_SUMMARY ?? true,

  // Timing and thresholds
  GRACE_DAYS: Number(fromTrigger.GRACE_DAYS ?? 2),
  MONTHLY_MIN_THRESHOLD: Number(fromTrigger.MONTHLY_MIN_THRESHOLD ?? 0),
  VARIANCE_THRESHOLD: Number(fromTrigger.VARIANCE_THRESHOLD ?? 50), // Minimum â‚¬50 variance for alerts

  // Noise reduction settings
  BATCH_SIMILAR_ALERTS: fromTrigger.BATCH_SIMILAR_ALERTS ?? true,
  DUPLICATE_WINDOW_HOURS: Number(fromTrigger.DUPLICATE_WINDOW_HOURS ?? 24),
  MIN_UNEXPECTED_AMOUNT: Number(fromTrigger.MIN_UNEXPECTED_AMOUNT ?? 100), // Minimum â‚¬100 for unexpected income alerts

  // Integration flags
  UPDATE_BUDGET_PROJECTIONS: fromTrigger.UPDATE_BUDGET_PROJECTIONS ?? true,
  UPDATE_GOAL_PROGRESS: fromTrigger.UPDATE_GOAL_PROGRESS ?? true,
  SYNC_CASH_FLOW: fromTrigger.SYNC_CASH_FLOW ?? true,

  // Current period info
  CURRENT_MONTH: new Date().toISOString().slice(0, 7),
  TIMESTAMP: new Date().toISOString()
};

console.log('ðŸ“‹ Income Tracker Configuration:', config);
return config;

// Enhanced Income Categorization with Confidence Scoring
const currentMonth = $('Enhanced Input Adapter').first().json.CURRENT_MONTH;

// Enhanced categorization rules with confidence scoring
const incomeRules = {
  'Salary': {
    keywords: ['salary', 'payroll', 'wage', 'paycheck', 'net pay', 'pay day', 'monthly pay'],
    confidence: 'high',
    recurring: true,
    priority: 1
  },
  'Freelance': {
    keywords: ['invoice', 'freelance', 'contract', 'upwork', 'fiverr', 'stripe', 'paypal', 'client payment', 'project payment'],
    confidence: 'medium',
    recurring: false,
    priority: 2
  },
  'Dividends': {
    keywords: ['dividend', 'payout', 'distribution', 'investment income', 'stock dividend'],
    confidence: 'high',
    recurring: true,
    priority: 3
  },
  'Interest': {
    keywords: ['interest', 'yield', 'savings interest', 'isa', 'apy', 'bond interest'],
    confidence: 'high',
    recurring: true,
    priority: 4
  },
  'Benefits': {
    keywords: ['benefit', 'allowance', 'pension', 'social security', 'unemployment', 'disability'],
    confidence: 'high',
    recurring: true,
    priority: 5
  },
  'Refunds': {
    keywords: ['refund', 'reversal', 'chargeback', 'cashback', 'tax refund', 'insurance claim'],
    confidence: 'medium',
    recurring: false,
    priority: 6
  },
  'Side Income': {
    keywords: ['rental', 'side hustle', 'gig', 'uber', 'delivery', 'etsy', 'ebay', 'marketplace'],
    confidence: 'medium',
    recurring: false,
    priority: 7
  }
};

// Process current month income transactions
const processedIncome = $input.all()
  .filter(item => {
    const tx = item.json;
    const txMonth = (tx.month || '').slice(0, 7);
    const isIncome = String(tx.type || '').toLowerCase() === 'income';
    return txMonth === currentMonth && isIncome && tx.amount && Number(tx.amount) > 0;
  })
  .map(item => {
    const tx = item.json;
    const searchText = `${tx.purpose || ''} ${tx.category || ''} ${tx.merchant || ''}`.toLowerCase();

    // Check if already properly categorized
    let assignedCategory = null;
    let confidence = 'low';
    let matchedRule = null;

    if (tx.category && Object.keys(incomeRules).includes(tx.category)) {
      assignedCategory = tx.category;
      confidence = incomeRules[tx.category].confidence;
    } else {
      // Find best matching rule
      const matches = [];

      for (const [category, rule] of Object.entries(incomeRules)) {
        const keywordMatches = rule.keywords.filter(keyword => 
          searchText.includes(keyword.toLowerCase())
        ).length;

        if (keywordMatches > 0) {
          matches.push({
            category,
            score: keywordMatches * (rule.priority <= 3 ? 2 : 1), // Boost common categories
            confidence: rule.confidence,
            rule
          });
        }
      }

      // Select best match
      if (matches.length > 0) {
        const bestMatch = matches.sort((a, b) => b.score - a.score)[0];
        assignedCategory = bestMatch.category;
        confidence = bestMatch.confidence;
        matchedRule = bestMatch.rule;
      } else {
        assignedCategory = 'Income-Other';
        confidence = 'low';
      }
    }

    return {
      json: {
        ...tx,
        category: assignedCategory,
        confidence: confidence,
        recurring: matchedRule ? matchedRule.recurring : false,
        _ts: tx.date,
        _amt: Number(tx.amount || 0),
        _purpose: (tx.purpose || '').trim(),
        _merchant: (tx.merchant || '').trim(),
        _processed_at: new Date().toISOString()
      }
    };
  });

console.log(`ðŸ’° Processed ${processedIncome.length} income transactions for ${currentMonth}`);
if (processedIncome.length > 0) {
  console.log('ðŸ“Š Category breakdown:', 
    processedIncome.reduce((acc, item) => {
      const cat = item.json.category;
      acc[cat] = (acc[cat] || 0) + 1;
      return acc;
    }, {})
  );
}

return processedIncome;

// Historical Trend Analysis
const config = $('Enhanced Input Adapter').first().json;
const currentMonth = config.CURRENT_MONTH;

// Get historical data for trend analysis
const historicalTransactions = $('Get Historical Income (3 months)').all().map(i => i.json);

// Calculate 3-month trends by category
const monthlyTotals = {};
const categoryTrends = {};

historicalTransactions
  .filter(tx => tx.type?.toLowerCase() === 'income' && tx.amount && Number(tx.amount) > 0)
  .forEach(tx => {
    const month = (tx.month || '').slice(0, 7);
    const category = tx.category || 'Uncategorized';
    const amount = Number(tx.amount);

    if (!monthlyTotals[month]) monthlyTotals[month] = 0;
    monthlyTotals[month] += amount;

    if (!categoryTrends[category]) categoryTrends[category] = {};
    if (!categoryTrends[category][month]) categoryTrends[category][month] = 0;
    categoryTrends[category][month] += amount;
  });

// Calculate trend indicators
const months = Object.keys(monthlyTotals).sort().slice(-3); // Last 3 months
const trendData = {
  monthlyTotals,
  categoryTrends,
  recentMonths: months,
  averageMonthlyIncome: months.length > 0 ? 
    months.reduce((sum, month) => sum + (monthlyTotals[month] || 0), 0) / months.length : 0,
  trendDirection: 'stable'
};

// Determine trend direction
if (months.length >= 2) {
  const recent = monthlyTotals[months[months.length - 1]] || 0;
  const previous = monthlyTotals[months[months.length - 2]] || 0;
  const changePercent = previous > 0 ? ((recent - previous) / previous) * 100 : 0;

  if (changePercent > 10) trendData.trendDirection = 'increasing';
  else if (changePercent < -10) trendData.trendDirection = 'decreasing';

  trendData.trendChange = changePercent;
}

console.log('ðŸ“ˆ Trend Analysis:', trendData);
return { json: trendData };

// Enhanced Alert Generation with Noise Reduction
const config = $('Enhanced Input Adapter').first().json;
const income = $('Enhanced Income Categorization').all().map(i => i.json);
const expected = $('Get Expected Income (Current Month)').all().map(i => i.json);
const trendData = $('Historical Trend Analysis').first().json;

const currentMonth = config.CURRENT_MONTH;
const today = new Date();

// Helper functions
function withinDays(dateA, dateB, toleranceDays) {
  if (!dateA || !dateB) return false;
  return Math.abs((dateA - dateB) / (1000 * 3600 * 24)) <= toleranceDays;
}

function parseDate(dateStr) {
  if (!dateStr) return null;
  const date = new Date(dateStr);
  return isNaN(date.getTime()) ? null : date;
}

function formatCurrency(amount) {
  return `â‚¬${Number(amount || 0).toFixed(2)}`;
}

function formatDate(dateStr) {
  return (dateStr || '').slice(0, 10);
}

// Calculate monthly totals
const monthlyTotal = income.reduce((sum, tx) => sum + (Number(tx._amt) || 0), 0);
const highConfidenceTotal = income
  .filter(tx => tx.confidence === 'high')
  .reduce((sum, tx) => sum + (Number(tx._amt) || 0), 0);

// Alert generation with severity classification
const alerts = [];
const processedSources = new Set();

console.log(`ðŸ’° Processing alerts for ${income.length} transactions, ${expected.length} expected incomes`);

// 1. EXPECTED INCOME TRACKING
expected
  .filter(exp => exp.active !== false && exp.source && exp.source.trim())
  .forEach(expectedIncome => {
    const source = expectedIncome.source.trim();
    const expectedDate = parseDate(expectedIncome.expected_date);
    const toleranceDays = Number(expectedIncome.tolerance_days || config.GRACE_DAYS);
    const expectedAmount = Number(expectedIncome.amount || 0);

    if (processedSources.has(source.toLowerCase())) return;
    processedSources.add(source.toLowerCase());

    // Find matching transaction
    const matchedTransaction = income.find(tx => 
      tx._purpose.toLowerCase().includes(source.toLowerCase()) ||
      tx._merchant.toLowerCase().includes(source.toLowerCase())
    );

    if (matchedTransaction) {
      const txDate = parseDate(matchedTransaction.date);
      const variance = Math.abs(matchedTransaction._amt - expectedAmount);
      const variancePercent = expectedAmount > 0 ? (variance / expectedAmount) * 100 : 0;

      // Check if within expected timeframe
      if (expectedDate && txDate && withinDays(txDate, expectedDate, toleranceDays)) {
        // Expected income received
        let severity = 'info';
        let message = `âœ… **Income received**: ${source}\nAmount: ${formatCurrency(matchedTransaction._amt)} on ${formatDate(matchedTransaction.date)}`;

        // Check for significant variance
        if (variance > config.VARIANCE_THRESHOLD && variancePercent > 15) {
          severity = 'warning';
          message += `\nâš ï¸ Variance: ${formatCurrency(variance)} (${variancePercent.toFixed(1)}%) from expected ${formatCurrency(expectedAmount)}`;
        }

        alerts.push({
          kind: 'deposit_received',
          severity,
          source,
          amount: matchedTransaction._amt,
          expected_amount: expectedAmount,
          variance,
          variance_percent: variancePercent,
          date: matchedTransaction.date,
          message,
          confidence: matchedTransaction.confidence
        });
      }
    } else if (expectedDate) {
      // Check if income is overdue
      const cutoffDate = new Date(expectedDate.getTime() + toleranceDays * 24 * 60 * 60 * 1000);

      if (today > cutoffDate) {
        const daysLate = Math.floor((today - cutoffDate) / (1000 * 3600 * 24));
        const severity = daysLate > 7 ? 'critical' : daysLate > 3 ? 'warning' : 'info';

        alerts.push({
          kind: 'income_missing',
          severity,
          source,
          expected_amount: expectedAmount,
          expected_date: expectedIncome.expected_date,
          days_late: daysLate,
          message: `âš ï¸ **Expected income missing**: ${source}\nExpected: ${formatCurrency(expectedAmount)} by ${formatDate(expectedIncome.expected_date)}\nStatus: ${daysLate} days overdue`
        });
      }
    }
  });

// 2. UNEXPECTED INCOME DETECTION
const expectedSources = expected
  .map(exp => exp.source?.toLowerCase()?.trim())
  .filter(Boolean);

income
  .filter(tx => tx._amt >= config.MIN_UNEXPECTED_AMOUNT)
  .forEach(tx => {
    const purpose = tx._purpose.toLowerCase();
    const merchant = tx._merchant.toLowerCase();

    const isExpected = expectedSources.some(source => 
      purpose.includes(source) || merchant.includes(source)
    );

    if (!isExpected && tx.confidence !== 'high') {
      const severity = tx._amt > 1000 ? 'warning' : 'info';

      alerts.push({
        kind: 'unexpected_income',
        severity,
        source: tx._purpose || tx._merchant || 'Unknown',
        amount: tx._amt,
        date: tx.date,
        category: tx.category,
        confidence: tx.confidence,
        message: `ðŸ’¡ **Unexpected income detected**\nSource: ${tx._purpose || tx._merchant || 'Unknown'}\nAmount: ${formatCurrency(tx._amt)} on ${formatDate(tx.date)}\nCategory: ${tx.category}`
      });
    }
  });

// 3. THRESHOLD MONITORING
const sheetThreshold = Math.max(...expected.map(exp => Number(exp.threshold_min_month || 0)), 0);
const effectiveThreshold = Math.max(sheetThreshold, config.MONTHLY_MIN_THRESHOLD);

if (effectiveThreshold > 0 && monthlyTotal < effectiveThreshold) {
  const shortfall = effectiveThreshold - monthlyTotal;
  const shortfallPercent = effectiveThreshold > 0 ? (shortfall / effectiveThreshold) * 100 : 0;

  alerts.push({
    kind: 'threshold_drop',
    severity: shortfallPercent > 50 ? 'critical' : shortfallPercent > 25 ? 'warning' : 'info',
    threshold: effectiveThreshold,
    monthly_total: monthlyTotal,
    shortfall,
    shortfall_percent: shortfallPercent,
    message: `ðŸ“‰ **Income below threshold**\nThis month: ${formatCurrency(monthlyTotal)}\nThreshold: ${formatCurrency(effectiveThreshold)}\nShortfall: ${formatCurrency(shortfall)} (${shortfallPercent.toFixed(1)}%)`
  });
}

// 4. TREND ANALYSIS ALERTS
if (trendData.trendDirection === 'decreasing' && trendData.trendChange < -20) {
  alerts.push({
    kind: 'trend_decline',
    severity: 'warning',
    trend_change: trendData.trendChange,
    average_monthly: trendData.averageMonthlyIncome,
    current_monthly: monthlyTotal,
    message: `ðŸ“‰ **Income trend declining**\nCurrent month: ${formatCurrency(monthlyTotal)}\n3-month average: ${formatCurrency(trendData.averageMonthlyIncome)}\nTrend: ${trendData.trendChange.toFixed(1)}% decline`
  });
}

// 5. SUMMARY GENERATION
const summaryMessages = [];

if (config.POST_SUMMARY) {
  const categoryBreakdown = income.reduce((acc, tx) => {
    acc[tx.category] = (acc[tx.category] || 0) + tx._amt;
    return acc;
  }, {});

  const sortedCategories = Object.entries(categoryBreakdown)
    .sort((a, b) => b[1] - a[1])
    .map(([category, amount]) => `â€¢ ${category}: ${formatCurrency(amount)}`);

  summaryMessages.push({
    kind: 'summary',
    severity: 'info',
    monthly_total: monthlyTotal,
    transaction_count: income.length,
    category_breakdown: categoryBreakdown,
    message: `ðŸ§¾ **Income summary** (${currentMonth})\nTotal: ${formatCurrency(monthlyTotal)} from ${income.length} transactions\n${sortedCategories.join('\n')}`
  });
}

// Combine alerts and summaries
const allMessages = [...alerts, ...summaryMessages];

// Apply noise reduction
const filteredMessages = allMessages.filter(msg => {
  // Filter out low-severity info messages if there are many alerts
  if (msg.severity === 'info' && alerts.length > 5) {
    return msg.kind === 'summary' || msg.kind === 'deposit_received';
  }
  return true;
});

console.log(`ðŸ”” Generated ${filteredMessages.length} alerts (${alerts.length} alerts + ${summaryMessages.length} summaries)`);
if (filteredMessages.length > 0) {
  console.log('Alert breakdown by severity:', 
    filteredMessages.reduce((acc, msg) => {
      acc[msg.severity] = (acc[msg.severity] || 0) + 1;
      return acc;
    }, {})
  );
}

return filteredMessages.map(msg => ({ json: msg }));

// Budget Integration Data Preparation
const config = $('Enhanced Input Adapter').first().json;
const income = $('Enhanced Income Categorization').all().map(i => i.json);
const alerts = $('Enhanced Alert Engine').all().map(i => i.json);

// Calculate income projections for budget system
const monthlyTotal = income.reduce((sum, tx) => sum + (Number(tx._amt) || 0), 0);
const recurringIncome = income
  .filter(tx => tx.recurring === true)
  .reduce((sum, tx) => sum + (Number(tx._amt) || 0), 0);

const oneTimeIncome = monthlyTotal - recurringIncome;

// Detect critical issues that affect budgeting
const criticalIssues = alerts.filter(alert => alert.severity === 'critical');
const hasIncomeShortfall = alerts.some(alert => alert.kind === 'threshold_drop' || alert.kind === 'income_missing');

// Prepare budget integration payload
const budgetIntegrationData = {
  month: config.CURRENT_MONTH,
  total_income: monthlyTotal,
  recurring_income: recurringIncome,
  one_time_income: oneTimeIncome,
  income_confidence: income.filter(tx => tx.confidence === 'high').length / Math.max(income.length, 1),
  has_income_issues: criticalIssues.length > 0,
  income_shortfall: hasIncomeShortfall,
  recommendation: hasIncomeShortfall ? 'review_budget_targets' : 'maintain_current_budget',
  last_updated: config.TIMESTAMP
};

console.log('ðŸ’¼ Budget Integration Data:', budgetIntegrationData);
return budgetIntegrationData;

// Goal Integration Data Preparation
const config = $('Enhanced Input Adapter').first().json;
const income = $('Enhanced Income Categorization').all().map(i => i.json);

// Identify savings-related income
const savingsIncome = income.filter(tx => 
  tx.category === 'Interest' || 
  tx.category === 'Dividends' ||
  tx._purpose.toLowerCase().includes('savings') ||
  tx._purpose.toLowerCase().includes('investment')
);

const totalSavingsIncome = savingsIncome.reduce((sum, tx) => sum + tx._amt, 0);
const totalIncome = income.reduce((sum, tx) => sum + tx._amt, 0);

// Prepare goal progress data
const goalProgressData = {
  month: config.CURRENT_MONTH,
  total_income: totalIncome,
  savings_income: totalSavingsIncome,
  available_for_goals: totalIncome * 0.2, // Assume 20% can go to goals
  income_stability: income.filter(tx => tx.confidence === 'high').length / Math.max(income.length, 1),
  last_updated: config.TIMESTAMP,
  trigger_goal_review: totalIncome > 0
};

console.log('ðŸŽ¯ Goal Integration Data:', goalProgressData);
return goalProgressData;

// Comprehensive Response Assembly for AI Agent
const config = $('Enhanced Input Adapter').first().json;
const income = $('Enhanced Income Categorization').all().map(i => i.json);
const alerts = $('Enhanced Alert Engine').all().map(i => i.json);

// Separate alerts by type for structured response
const criticalAlerts = alerts.filter(alert => alert.severity === 'critical');
const warningAlerts = alerts.filter(alert => alert.severity === 'warning');
const infoAlerts = alerts.filter(alert => alert.severity === 'info');

// Calculate key metrics
const totalIncome = income.reduce((sum, tx) => sum + tx._amt, 0);
const transactionCount = income.length;
const categoryBreakdown = income.reduce((acc, tx) => {
  acc[tx.category] = (acc[tx.category] || 0) + tx._amt;
  return acc;
}, {});

// Determine overall status
let overallStatus = 'healthy';
if (criticalAlerts.length > 0) overallStatus = 'critical';
else if (warningAlerts.length > 0) overallStatus = 'warning';
else if (infoAlerts.length > 3) overallStatus = 'attention';

// Generate recommendations
const recommendations = [];
if (criticalAlerts.some(a => a.kind === 'income_missing')) {
  recommendations.push('Follow up on missing expected income immediately');
}
if (warningAlerts.some(a => a.kind === 'threshold_drop')) {
  recommendations.push('Review monthly budget targets - income below threshold');
}
if (alerts.some(a => a.kind === 'unexpected_income' && a.amount > 500)) {
  recommendations.push('Consider allocating unexpected income to savings goals');
}
if (recommendations.length === 0) {
  recommendations.push('Income tracking is on track - no immediate actions needed');
}

// Assemble final response
const response = {
  status: overallStatus,
  month: config.CURRENT_MONTH,
  summary: {
    total_income: totalIncome,
    transaction_count: transactionCount,
    category_breakdown: categoryBreakdown
  },
  alerts: {
    critical: criticalAlerts.length,
    warning: warningAlerts.length,
    info: infoAlerts.length,
    total: alerts.length
  },
  alert_details: alerts.map(alert => ({
    type: alert.kind,
    severity: alert.severity,
    message: alert.message,
    source: alert.source,
    amount: alert.amount
  })),
  recommendations,
  integrations: {
    budget_updated: config.UPDATE_BUDGET_PROJECTIONS,
    goals_updated: config.UPDATE_GOAL_PROGRESS,
    cash_flow_synced: config.SYNC_CASH_FLOW
  },
  next_actions: recommendations,
  processed_at: config.TIMESTAMP
};

console.log(`ðŸ“Š Income Tracker Response: ${overallStatus} status with ${alerts.length} alerts`);
return response;

