rts.push({kind:'threshold_drop',threshold,monthlyTotal,note:`Income this month â‚¬${monthlyTotal.toFixed(2)} < threshold â‚¬${threshold.toFixed(2)}`}); } function fmt(n){return `â‚¬${Number(n||0).toFixed(2)}`} function short(d){return (d||'').slice(0,10)} const msgs=[]; for(const a of alerts){ if(a.kind==='deposit_received'){ msgs.push({text:`âœ… *Income received*: ${a.source}
