// --- FINAL, CORRECTED CODE ---

const items = $input.all();
const tx = items.map(i => i.json);

// 1. Helper Function to Convert Excel Serial Date to JS Date
// This is the key fix to handle the numeric date format from your sheet.
function excelSerialDateToJSDate(serial) {
  if (typeof serial !== 'number' || isNaN(serial)) {
    return null; // Return null for invalid input
  }
  // Formula to convert Excel's serial number to a JavaScript Date object.
  // 25569 is the number of days between Excel's epoch (1900) and JS's epoch (1970).
  const utc_days = Math.floor(serial - 25569);
  const date = new Date(utc_days * 86400 * 1000);
  // Adjust for timezone offset to prevent off-by-one day errors
  const timezoneOffset = date.getTimezoneOffset() * 60000;
  return new Date(date.getTime() + timezoneOffset);
}


// 2. Data Cleaning & Preparation
const clean = tx.filter(r => r.Amount && r.Date && !['transfer','refund','income'].includes((r.Type||'').toLowerCase()));
clean.forEach(r => {
    r.DateObj = excelSerialDateToJSDate(r.Date);
});

const validTx = clean.filter(r => r.DateObj); // Work only with validly parsed dates

const now = new Date();

// CRITICAL CHECK: Verify there's data to analyze.
if (validTx.length === 0) {
  return [{ json: { error: "No valid transactions found. Please check your data source." } }];
}
const recentTx = validTx.filter(r => r.DateObj >= new Date(new Date().setDate(now.getDate() - 90)));
if (recentTx.length === 0) {
  return [{ json: { error: "No transactions found in the last 90 days." } }];
}


// 3. Helper function to aggregate spending
function aggregate(data) {
  const totals = {};
  data.forEach(r => {
    const cat = r.Category || 'Uncategorized';
    const amt = Math.abs(parseFloat(r.Amount));
    if (!totals[cat]) totals[cat] = 0;
    totals[cat] += amt;
  });
  return totals;
}

// 4. Main report building function
function buildReport(days, label) {
  const currentStart = new Date(new Date().setDate(now.getDate() - days));
  const prevStart = new Date(new Date().setDate(now.getDate() - (days * 2)));
  const avgStart = new Date(new Date().setDate(now.getDate() - 90));

  const currentTx = validTx.filter(r => r.DateObj >= currentStart && r.DateObj <= now);
  const prevTx = validTx.filter(r => r.DateObj >= prevStart && r.DateObj < currentStart);
  const avgTx = validTx.filter(r => r.DateObj >= avgStart && r.DateObj < now);

  const currentTotals = aggregate(currentTx);
  const prevTotals = aggregate(prevTx);
  const avgTotals = aggregate(avgTx);

  const allCategories = new Set([...Object.keys(currentTotals), ...Object.keys(prevTotals), ...Object.keys(avgTotals)]);
  const rows = [];

  allCategories.forEach(cat => {
    const currentAmount = currentTotals[cat] || 0;
    const prevAmount = prevTotals[cat] || 0;
    const threeMonthTotal = avgTotals[cat] || 0;
    const monthlyAvg = threeMonthTotal / 3;

    const diffVsPrev = currentAmount - prevAmount;
    const changeVsPrevPct = prevAmount > 0 ? ((diffVsPrev / prevAmount) * 100).toFixed(1) : (currentAmount > 0 ? 'NEW' : '0');
    const diffVsAvg = currentAmount - monthlyAvg;
    const changeVsAvgPct = monthlyAvg > 0 ? ((diffVsAvg / monthlyAvg) * 100).toFixed(1) : (currentAmount > 0 ? 'NEW' : '0');

    rows.push({
      'Period': label,
      'Category': cat,
      'Current': currentAmount,
      'Previous': prevAmount,
      'Average': monthlyAvg,
      'Diff Vs Prev': diffVsPrev,
      'Change Vs Prev': changeVsPrevPct,
      'Diff Vs Avg': diffVsAvg,
      'Change Vs Avg': changeVsAvgPct,
      'Timestamp': now.toISOString()
    });
  });

  const totalCurrent = Object.values(currentTotals).reduce((a, b) => a + b, 0);
  const totalPrev = Object.values(prevTotals).reduce((a, b) => a + b, 0);
  const totalAvg = Object.values(avgTotals).reduce((a, b) => a + b, 0) / 3;
  rows.push({
    'Period': label,
    'Category': "TOTAL",
    'Current': totalCurrent,
    'Previous': totalPrev,
    'Average': totalAvg,
    'Diff Vs Prev': totalCurrent - totalPrev,
    'Change Vs Prev': totalPrev > 0 ? (((totalCurrent - totalPrev) / totalPrev) * 100).toFixed(1) : 'NEW',
    'Diff Vs Avg': totalCurrent - totalAvg,
    'Change Vs Avg': totalAvg > 0 ? (((totalCurrent - totalAvg) / totalAvg) * 100).toFixed(1) : 'NEW',
    'Timestamp': now.toISOString()
  });

  return rows;
}

// 5. Generate and return the reports
return [
  { json: { period: "WEEKLY", data: buildReport(7, "WEEKLY") } },
  { json: { period: "MONTHLY", data: buildReport(30, "MONTHLY") } }
];

// This function builds the final user-facing summary.
function summarize(data, label) {
  // Graceful handling if there's not enough data for a period.
  if (!data || data.length === 0) {
    return `ðŸ“Š ${label} Spending Report\n\nNot enough data to generate a report for this period.`;
  }

  const total = data.find(r => r.Category === "TOTAL");
  if (!total) {
      return `ðŸ“Š ${label} Spending Report\n\nCould not calculate totals for this period.`;
  }
  const cats = data.filter(r => r.Category !== "TOTAL");

  // 1. Separate Habits from One-Offs
  const sortedByDiff = [...cats].sort((a, b) => Math.abs(b['Diff Vs Avg']) - Math.abs(a['Diff Vs Avg']));
  const notablePurchase = sortedByDiff[0];
  const habitChanges = sortedByDiff.slice(1, 3);

  // 2. Build the summary text with context (vs average)
  let summary = `ðŸ“Š ${label} Spending Report\n\n`;
  summary += `Total spending was â‚¬${total.Current.toFixed(2)}. `;
  summary += `This is ${total['Change Vs Avg'] > 0 ? 'ðŸ”º' : 'âœ…'} ${Math.abs(total['Change Vs Avg'])}% compared to your 3-month average of â‚¬${total.Average.toFixed(2)}/period.\n\n`;

  // Highlight the single most notable purchase
  if (notablePurchase && Math.abs(notablePurchase['Diff Vs Avg']) > 50) {
    summary += `â–ªï¸ Notable Purchase: Your spending on ${notablePurchase.Category} was â‚¬${notablePurchase.Current.toFixed(0)}, which is â‚¬${Math.abs(notablePurchase['Diff Vs Avg']).toFixed(0)} ${notablePurchase['Diff Vs Avg'] > 0 ? 'more' : 'less'} than usual.\n`;
  }

  // Highlight the top 2 habit changes
  if (habitChanges.length > 0) {
    summary += 'â–ªï¸ Top Habit Changes:\n';
    habitChanges.forEach(c => {
      if(Math.abs(c['Diff Vs Avg']) > 20){
         summary += `  â€¢ ${c.Category} was ${c['Diff Vs Avg'] > 0 ? 'up' : 'down'} by â‚¬${Math.abs(c['Diff Vs Avg']).toFixed(0)} compared to your average.\n`;
      }
    });
  }

  return summary;
}

// Extract the weekly and monthly data with error checking
const weeklyItem = items.find(i => i.json && i.json.period === "WEEKLY");
const monthlyItem = items.find(i => i.json && i.json.period === "MONTHLY");

// If there's no monthly data, we can't proceed.
if (!monthlyItem) {
  return [{ json: { text: "Could not generate the monthly spending report. Not enough transaction data." } }];
}

const weeklyData = weeklyItem ? weeklyItem.json.data : null;
const monthlyData = monthlyItem.json.data;

// Create the final text output
const finalText = summarize(monthlyData, 'Monthly');

// Prepare a structured JSON output for your app
const jsonForApp = {
  weeklyReport: weeklyData ? summarize(weeklyData, 'Weekly') : "Weekly report not available.",
  monthlyReport: finalText
}

return [
  {
    json: {
      text: finalText,
      appData: jsonForApp
    }
  }
];
