// Extract verification response from webhook
const { action, flightId, userEmail } = $input.first().json.body;

// Validate required parameters
if (!action || !flightId) {
  throw new Error('Missing required parameters: action or flightId');
}

// Process verification response
const verification = {
  action: action.toUpperCase(),
  flightId: flightId,
  userEmail: userEmail,
  timestamp: new Date().toISOString(),
  verified: action.toUpperCase() === 'CONFIRM'
};

// Log verification attempt
console.log(`Flight verification: ${verification.action} for flight ${verification.flightId}`);

return verification;

