// Extract flight search parameters
const body = $input.first().json.body;

return [{
  json: {
    origin: body.origin || '',
    destination: body.destination || '',
    departureDate: body.departureDate || '',
    returnDate: body.returnDate || '',
    passengers: body.passengers || 1,
    class: body.class || 'economy',
    userEmail: body.userEmail || '',
    userPhone: body.userPhone || '',
    slackChannel: body.slackChannel || '',
    telegramChatId: body.telegramChatId || '',
    searchId: Math.random().toString(36).substr(2, 9)
  }
}];

// Process flight search results
const searchData = $input.first().json;
const flightOffers = searchData.data || [];

if (flightOffers.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No flights found for your search criteria',
      searchParams: $('Process Search Parameters').first().json,
      flights: []
    }
  }];
}

// Format flight results
const formattedFlights = flightOffers.slice(0, 5).map((offer, index) => {
  const outbound = offer.itineraries[0];
  const inbound = offer.itineraries[1] || null;

  return {
    id: offer.id,
    rank: index + 1,
    price: {
      total: offer.price.total,
      currency: offer.price.currency
    },
    outbound: {
      departure: outbound.segments[0].departure,
      arrival: outbound.segments[outbound.segments.length - 1].arrival,
      duration: outbound.duration,
      stops: outbound.segments.length - 1,
      airline: outbound.segments[0].carrierCode
    },
    inbound: inbound ? {
      departure: inbound.segments[0].departure,
      arrival: inbound.segments[inbound.segments.length - 1].arrival,
      duration: inbound.duration,
      stops: inbound.segments.length - 1,
      airline: inbound.segments[0].carrierCode
    } : null
  };
});

// Create notification message
const searchParams = $('Process Search Parameters').first().json;
let message = `ðŸ›« Flight Search Results Found!\n\n`;
message += `Search Details:\n`;
message += `ðŸ“ ${searchParams.origin} âž¡ï¸ ${searchParams.destination}\n`;
message += `ðŸ“… ${searchParams.departureDate}`;
if (searchParams.returnDate) {
  message += ` - ${searchParams.returnDate}`;
}
message += `\nðŸ‘¥ ${searchParams.passengers} passenger(s)\n\n`;

message += `Top Flight Options:\n`;
formattedFlights.forEach(flight => {
  message += `\n${flight.rank}. ${flight.price.currency} ${flight.price.total}\n`;
  message += `   Outbound: ${flight.outbound.departure.iataCode} â†’ ${flight.outbound.arrival.iataCode}\n`;
  message += `   ${flight.outbound.departure.at} - ${flight.outbound.arrival.at}\n`;
  message += `   Duration: ${flight.outbound.duration} | Stops: ${flight.outbound.stops}\n`;

  if (flight.inbound) {
    message += `   Return: ${flight.inbound.departure.iataCode} â†’ ${flight.inbound.arrival.iataCode}\n`;
    message += `   ${flight.inbound.departure.at} - ${flight.inbound.arrival.at}\n`;
  }
});

message += `\n\nPlease confirm your preferred option to proceed with booking.`;

return [{
  json: {
    success: true,
    message: 'Flights found successfully',
    searchParams: searchParams,
    flights: formattedFlights,
    notificationMessage: message,
    emailSubject: `Flight Search Results: ${searchParams.origin} to ${searchParams.destination}`,
    htmlMessage: message.replace(/\n/g, '<br>')
  }
}];

