// Handle API errors and format error response
const error = $input.first();
const searchParams = $('Process Search Parameters').first().json;

return [{
  json: {
    success: false,
    message: 'Error searching for flights. Please try again later.',
    error: error.error?.message || 'Unknown error',
    searchParams: searchParams
  }
}];
