const input = $input.first().json;
const userMessage = input.chatInput || input.query || input.input || '';

const extractDetails = (message) => {
  try {
    const text = message.toLowerCase();

    const mealPatterns = [
      /(\d+)\s*(meal|recipe|dish|course)/i,
      /\b(one|two|three|four|five|six|seven)\s*(meal|recipe|dish)/i,
      /(\d+)\s*different/i
    ];

    let meals = 3;
    const numberMap = { one: 1, two: 2, three: 3, four: 4, five: 5, six: 6, seven: 7 };

    for (const pattern of mealPatterns) {
      const match = text.match(pattern);
      if (match) {
        const value = match[1].toLowerCase();
        meals = numberMap[value] || parseInt(value) || 3;
        break;
      }
    }

    const servingPatterns = [
      /(\d+)\s*(people|person|serving|guest)/i,
      /for\s+(\d+)/i,
      /feeds?\s+(\d+)/i
    ];

    let servings = 4;
    for (const pattern of servingPatterns) {
      const match = text.match(pattern);
      if (match) {
        servings = parseInt(match[1]) || 4;
        break;
      }
    }

    const mealTypes = {
      breakfast: /\b(breakfast|morning|brunch|cereal|pancake|oatmeal)\b/i,
      lunch: /\b(lunch|midday|sandwich|salad|wrap)\b/i,
      dinner: /\b(dinner|evening|supper|main course)\b/i,
      snack: /\b(snack|appetizer|finger food)\b/i,
      dessert: /\b(dessert|sweet|cake|cookie|pie)\b/i
    };

    let mealType = 'dinner';
    for (const [type, pattern] of Object.entries(mealTypes)) {
      if (pattern.test(text)) {
        mealType = type;
        break;
      }
    }

    const dietaryOptions = {
      vegetarian: /\b(vegetarian|veggie)\b/i,
      vegan: /\b(vegan|plant.?based)\b/i,
      'gluten-free': /\b(gluten.?free|gf|celiac)\b/i,
      'dairy-free': /\b(dairy.?free|lactose.?free|no dairy)\b/i,
      healthy: /\b(healthy|nutritious|clean|wholesome)\b/i,
      'low-carb': /\b(low.?carb|keto|atkins)\b/i,
      'high-protein': /\b(protein|muscle|fitness|workout)\b/i,
      quick: /\b(quick|fast|easy|simple|30.?min|rapid)\b/i,
      'budget-friendly': /\b(budget|cheap|affordable|save money|low.?cost)\b/i,
      comfort: /\b(comfort|cozy|hearty|soul food)\b/i,
      fresh: /\b(fresh|seasonal|local|organic)\b/i,
      spicy: /\b(spicy|hot|kick|heat)\b/i
    };

    const dietary = [];
    for (const [pref, pattern] of Object.entries(dietaryOptions)) {
      if (pattern.test(text)) dietary.push(pref);
    }

    const cuisineTypes = {
      italian: /\b(italian|pasta|pizza|mediterranean)\b/i,
      mexican: /\b(mexican|taco|burrito|tex.?mex|salsa)\b/i,
      asian: /\b(asian|chinese|japanese|thai|korean|stir.?fry)\b/i,
      indian: /\b(indian|curry|tikka|masala|spice)\b/i,
      american: /\b(american|bbq|burger|southern)\b/i,
      french: /\b(french|classic|elegant)\b/i,
      greek: /\b(greek|tzatziki|feta|olive)\b/i,
      spanish: /\b(spanish|paella|tapas)\b/i
    };

    const cuisines = [];
    for (const [cuisine, pattern] of Object.entries(cuisineTypes)) {
      if (pattern.test(text)) cuisines.push(cuisine);
    }

    const timeConstraints = {
      'under-15-min': /\b(15.?min|quarter.?hour|very.?quick)\b/i,
      'under-30-min': /\b(30.?min|half.?hour|quick)\b/i,
      'under-1-hour': /\b(1.?hour|hour)\b/i,
      'slow-cook': /\b(slow.?cook|all.?day|crockpot|instant.?pot)\b/i,
      'meal-prep': /\b(meal.?prep|batch.?cook|make.?ahead|weekly.?prep)\b/i
    };

    const timePrefs = [];
    for (const [time, pattern] of Object.entries(timeConstraints)) {
      if (pattern.test(text)) timePrefs.push(time);
    }

    const equipment = {
      'slow-cooker': /\b(slow.?cooker|crockpot|crock.?pot)\b/i,
      'instant-pot': /\b(instant.?pot|pressure.?cooker)\b/i,
      'air-fryer': /\b(air.?fryer|air.?fry)\b/i,
      oven: /\b(oven|bake|roast)\b/i,
      grill: /\b(grill|bbq|barbecue)\b/i,
      stovetop: /\b(stovetop|pan|skillet)\b/i
    };

    const preferredEquipment = [];
    for (const [equip, pattern] of Object.entries(equipment)) {
      if (pattern.test(text)) preferredEquipment.push(equip);
    }

    // Extract common ingredients like in recipe search
    const commonIngredients = {
      proteins: ['chicken', 'beef', 'pork', 'fish', 'salmon', 'turkey', 'tofu', 'eggs', 'shrimp', 'lamb'],
      vegetables: ['broccoli', 'spinach', 'mushrooms', 'tomatoes', 'onions', 'peppers', 'carrots', 'zucchini', 'eggplant'],
      grains: ['rice', 'pasta', 'quinoa', 'bread', 'noodles', 'barley', 'couscous'],
      dairy: ['cheese', 'milk', 'yogurt', 'cream', 'butter'],
      herbs: ['basil', 'oregano', 'thyme', 'rosemary', 'cilantro', 'parsley']
    };

    const ingredients = [];
    for (const [category, items] of Object.entries(commonIngredients)) {
      for (const item of items) {
        if (text.includes(item)) ingredients.push(item);
      }
    }

    return {
      meals: Math.min(Math.max(meals, 1), 7),
      servings: Math.min(Math.max(servings, 1), 12),
      mealType,
      dietary,
      cuisines,
      timePrefs,
      preferredEquipment,
      ingredients,
      originalMessage: message,
      error: null
    };
  } catch (error) {
    return {
      meals: 3,
      servings: 4,
      mealType: 'dinner',
      dietary: [],
      cuisines: [],
      timePrefs: [],
      preferredEquipment: [],
      ingredients: [],
      originalMessage: message,
      error: error.message
    };
  }
};

const calculateComplexity = (details, message) => {
  let score = 0;

  if (message.length > 100) score += 2;
  else if (message.length > 50) score += 1;

  score += details.dietary.length;
  score += details.cuisines.length;
  score += details.timePrefs.length;
  score += details.ingredients.length;

  if (details.meals > 3) score += 1;
  if (details.servings > 6) score += 1;

  return score >= 4 ? 'detailed' : score >= 2 ? 'moderate' : 'simple';
};

const details = extractDetails(userMessage);
const complexity = calculateComplexity(details, userMessage);

return {
  mealCount: details.meals,
  servings: details.servings,
  mealType: details.mealType,
  dietaryPreferences: details.dietary,
  cuisineTypes: details.cuisines,
  timeConstraints: details.timePrefs,
  preferredEquipment: details.preferredEquipment,
  ingredients: details.ingredients,
  originalMessage: details.originalMessage,
  sessionId: input.sessionId,
  timestamp: new Date().toISOString(),
  complexity: complexity,
  error: details.error
};

const details = $input.first().json;

return {
  output: `I apologize, but I encountered an issue processing your meal planning request. Let me help you with a simple meal suggestion instead.\n\nHow about I suggest ${details.mealCount} delicious ${details.mealType} recipes for ${details.servings} people? Just let me know any specific preferences you have!`,
  success: false,
  error: details.error,
  data: details,
  timestamp: new Date().toISOString(),
  source: 'meal_planning_error_handler'
};

const aiResponse = $input.first().json;
const mealDetails = $('Enhanced Detail Extraction').first().json;

// Fix: Get the response from the correct field (same as recipe search)
let response = aiResponse.output || 
               aiResponse.text || 
               aiResponse.content ||
               aiResponse.message ||
               (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message && aiResponse.choices[0].message.content) ||
               (typeof aiResponse === 'string' ? aiResponse : null);

// Debug: Log what we're getting from the AI
console.log('AI Response Keys:', Object.keys(aiResponse));
console.log('AI Response Type:', typeof aiResponse);
console.log('AI Response:', JSON.stringify(aiResponse).substring(0, 200) + '...');

// If we still don't have a response, provide a fallback
if (!response) {
  response = `I would be happy to help you plan some meals! Based on your request, I can suggest ${mealDetails.mealCount} ${mealDetails.mealType} recipes for ${mealDetails.servings} people. Could you tell me a bit more about what you are in the mood for?`;
  console.warn('No AI response found, using fallback');
} else {
  console.log('Found AI response, length:', response.length);
}

// Add context only if we have the actual AI response
const contextItems = [];
if (response.length > 100 && mealDetails.ingredients.length > 0) {
  contextItems.push(`featuring ${mealDetails.ingredients.join(', ')}`);
}
if (response.length > 100 && mealDetails.dietaryPreferences.length > 0) {
  contextItems.push(`perfectly suited for ${mealDetails.dietaryPreferences.join(' and ')} needs`);
}
if (response.length > 100 && mealDetails.cuisineTypes.length > 0) {
  contextItems.push(`focusing on ${mealDetails.cuisineTypes.join(' and ')} flavors`);
}
if (response.length > 100 && mealDetails.timeConstraints.length > 0) {
  contextItems.push(`designed for ${mealDetails.timeConstraints.join(' and ')} cooking`);
}

if (contextItems.length > 0 && response.length > 100) {
  response += `\n\nâœ¨ These meal plans are ${contextItems.join(', ')} - I hope they work perfectly for you!`;
}

return {
  output: response,
  success: true,
  data: {
    mealCount: mealDetails.mealCount,
    servings: mealDetails.servings,
    mealType: mealDetails.mealType,
    ingredients: mealDetails.ingredients,
    dietaryPreferences: mealDetails.dietaryPreferences,
    cuisineTypes: mealDetails.cuisineTypes,
    timeConstraints: mealDetails.timeConstraints,
    preferredEquipment: mealDetails.preferredEquipment,
    complexity: mealDetails.complexity
  },
  timestamp: new Date().toISOString(),
  source: 'enhanced_meal_planning_assistant',
  debug: {
    aiResponseKeys: Object.keys(aiResponse),
    responseLength: response ? response.length : 0,
    foundResponse: !!response
  }
};
