const input = $input.first().json;
let query = '';
let sessionId = null;

if (input.query) {
  query = input.query;
} else if (input.chatInput) {
  query = input.chatInput;
} else if (input.input) {
  query = input.input;
} else if (typeof input === 'string') {
  query = input;
} else {
  query = JSON.stringify(input);
}

sessionId = input.sessionId || null;

if (!query || query.trim().length < 3) {
  return {
    error: true,
    message: 'Please provide a specific product to search for.',
    suggestion: 'Examples: iPhone 15 Pro, vintage Nike shoes, MacBook Pro',
    hasQuery: false
  };
}

const originalQuery = query.trim();
const cleanedQuery = originalQuery.toLowerCase();

const stopWords = ['find', 'search', 'looking', 'for', 'buy', 'purchase', 'get', 'need', 'want', 'show', 'me'];
const keywords = cleanedQuery
  .replace(/[^a-zA-Z0-9\s-]/g, ' ')
  .split(' ')
  .filter(word => word.length > 2 && !stopWords.includes(word))
  .slice(0, 6)
  .join(' ');

const searchVariations = {
  amazon: {
    primary: keywords,
    withBrand: originalQuery,
    categorySpecific: keywords + ' ' + (cleanedQuery.includes('vintage') ? 'collectible' : 'new')
  },
  ebay: {
    primary: keywords,
    auction: keywords + ' auction',
    vintage: keywords + (cleanedQuery.includes('vintage') ? ' vintage collectible' : ' used'),
    buyItNow: keywords + ' buy it now'
  }
};

let category = 'general';
let confidence = 0;
let preferredPlatform = 'both';

const categories = {
  electronics: {
    keywords: ['phone', 'smartphone', 'mobile', 'iphone', 'android', 'tablet', 'ipad', 'laptop', 'computer', 'pc', 'macbook', 'headphones', 'earbuds', 'speaker', 'tv', 'monitor', 'camera', 'gaming', 'console'],
    platformPreference: 'amazon'
  },
  vintage: {
    keywords: ['vintage', 'antique', 'collectible', 'rare', 'classic', 'retro', 'old', 'original'],
    platformPreference: 'ebay'
  },
  fashion: {
    keywords: ['shoes', 'sneakers', 'boots', 'clothing', 'shirt', 'dress', 'pants', 'jeans', 'jacket', 'coat', 'sweater', 'hoodie', 'bag', 'purse', 'watch', 'jewelry'],
    platformPreference: 'both'
  },
  home: {
    keywords: ['furniture', 'chair', 'table', 'bed', 'sofa', 'couch', 'lamp', 'kitchen', 'appliance', 'vacuum', 'cleaning', 'decor', 'bedding'],
    platformPreference: 'amazon'
  }
};

for (const cat in categories) {
  const data = categories[cat];
  const matchCount = data.keywords.filter(keyword => cleanedQuery.includes(keyword)).length;
  if (matchCount > confidence) {
    confidence = matchCount;
    category = cat;
    preferredPlatform = data.platformPreference;
  }
}

const brands = {
  apple: ['apple', 'iphone', 'ipad', 'macbook', 'airpods', 'imac'],
  samsung: ['samsung', 'galaxy'],
  sony: ['sony', 'playstation', 'ps5', 'ps4'],
  nike: ['nike', 'air jordan', 'swoosh'],
  adidas: ['adidas', 'three stripes'],
  microsoft: ['microsoft', 'xbox', 'surface']
};

let detectedBrand = null;
for (const brand in brands) {
  if (brands[brand].some(keyword => cleanedQuery.includes(keyword))) {
    detectedBrand = brand;
    break;
  }
}

const specs = {
  hasSize: /\b(size|sz)\s*(\d+|xs|s|m|l|xl|xxl)\b/i.test(originalQuery),
  hasCapacity: /\b(\d+\s*(gb|tb|mb))\b/i.test(originalQuery),
  hasColor: /\b(black|white|red|blue|green|yellow|pink|gray|silver|gold)\b/i.test(originalQuery),
  hasPrice: /\b(under|below|less than|cheaper than)\s*\$?\d+\b/i.test(originalQuery),
  isVintage: /\b(vintage|antique|classic|retro|old|original|collectible)\b/i.test(originalQuery),
  isUsed: /\b(used|pre-owned|second hand|refurbished)\b/i.test(originalQuery),
  isNew: /\b(new|brand new|unopened)\b/i.test(originalQuery)
};

const platformConfig = {
  enableAmazon: true,
  enableEbay: true,
  enableGoogleShopping: false,
  primaryPlatform: preferredPlatform === 'ebay' ? 'ebay' : 'amazon',
  searchBoth: preferredPlatform === 'both'
};

let searchStrategy = 'balanced';
if (specs.isVintage || specs.isUsed) {
  searchStrategy = 'ebay-focused';
  platformConfig.primaryPlatform = 'ebay';
} else if (specs.isNew || category === 'electronics') {
  searchStrategy = 'amazon-focused';
  platformConfig.primaryPlatform = 'amazon';
} else {
  searchStrategy = 'dual-platform';
  platformConfig.searchBoth = true;
}

return {
  originalQuery: originalQuery,
  cleanedQuery: cleanedQuery,
  keywords: keywords,
  searchVariations: searchVariations,
  category: category,
  categoryConfidence: confidence,
  detectedBrand: detectedBrand,
  specifications: specs,
  platformConfig: platformConfig,
  preferredPlatform: preferredPlatform,
  sessionId: sessionId,
  timestamp: new Date().toISOString(),
  hasQuery: true,
  searchStrategy: searchStrategy
};

const queryData = $('Query Processor').first().json;
const originalQuery = queryData.originalQuery;
const category = queryData.category;
const detectedBrand = queryData.detectedBrand;
const specs = queryData.specifications;

if (!queryData.hasQuery) {
  return {
    success: false,
    error: 'validation_failed',
    message: 'Please provide a specific product to search for.',
    suggestions: ['iPhone 15 Pro', 'vintage Nike shoes', 'MacBook Pro M3', 'Samsung 4K TV'],
    originalQuery: originalQuery
  };
}

function generateMockProducts(query, category, brand, specs) {
  const products = [];
  const priceMatch = query.match(/\d+/);
  const basePrice = priceMatch ? parseInt(priceMatch[0]) : 
    category === 'electronics' ? 299 : category === 'vintage' ? 89 : 149;

  const productCount = 8 + Math.floor(Math.random() * 5);

  for (let i = 0; i < productCount; i++) {
    const isAmazon = Math.random() > 0.5;
    const platform = isAmazon ? 'amazon' : 'ebay';
    const priceVariation = 0.7 + (Math.random() * 0.6);
    const price = Math.round(basePrice * priceVariation * 100) / 100;

    const conditions = specs.isVintage ? ['vintage', 'rare', 'classic'] : 
                     specs.isUsed ? ['used', 'refurbished', 'open box'] :
                     ['new', 'brand new', 'latest'];

    const condition = conditions[Math.floor(Math.random() * conditions.length)];
    const colors = ['black', 'white', 'blue', 'red', 'silver'];
    const color = colors[Math.floor(Math.random() * colors.length)];

    const titleVariations = [
      condition + ' ' + query,
      query + ' ' + color,
      query + ' Pro',
      'Premium ' + query,
      query + ' - ' + condition
    ];

    const title = titleVariations[i % titleVariations.length];
    const amazonFeatures = ['Prime Eligible', 'Free Shipping', 'New', 'Warranty'];
    const ebayFeatures = ['Buy It Now', 'Best Offer', 'Free Returns', 'Top Rated'];

    const features = isAmazon ? 
      amazonFeatures.slice(0, 2 + Math.floor(Math.random() * 2)) :
      ebayFeatures.slice(0, 2 + Math.floor(Math.random() * 2));

    if (specs.isVintage) features.push('Collectible');
    if (specs.isNew) features.push('Brand New');

    products.push({
      rank: i + 1,
      title: title,
      price: '$' + price.toFixed(2),
      numericPrice: price,
      platform: platform,
      retailer: isAmazon ? 'Amazon' : 'eBay',
      url: 'https://' + platform + '.com/product-' + (i + 1),
      description: condition + ' ' + query + ' with ' + features.slice(0, 2).join(' and '),
      features: features,
      rating: (3.5 + Math.random() * 1.5).toFixed(1),
      category: category,
      relevanceScore: 6 + Math.floor(Math.random() * 4),
      affiliateEarnings: isAmazon ? '3-8%' : '1-4%'
    });
  }

  return products;
}

const mockProducts = generateMockProducts(originalQuery, category, detectedBrand, specs);

const sortedProducts = mockProducts.sort((a, b) => {
  if (queryData.searchStrategy === 'ebay-focused' && a.platform === 'ebay' && b.platform !== 'ebay') {
    return -1;
  }
  if (queryData.searchStrategy === 'amazon-focused' && a.platform === 'amazon' && b.platform !== 'amazon') {
    return -1;
  }
  if (a.relevanceScore !== b.relevanceScore) {
    return b.relevanceScore - a.relevanceScore;
  }
  return a.numericPrice - b.numericPrice;
});

sortedProducts.forEach((product, index) => {
  product.rank = index + 1;
});

const stats = {
  totalResults: sortedProducts.length,
  amazonResults: sortedProducts.filter(p => p.platform === 'amazon').length,
  ebayResults: sortedProducts.filter(p => p.platform === 'ebay').length,
  averagePrice: sortedProducts.reduce((sum, p) => sum + p.numericPrice, 0) / sortedProducts.length,
  priceRange: {
    min: Math.min(...sortedProducts.map(p => p.numericPrice)),
    max: Math.max(...sortedProducts.map(p => p.numericPrice))
  },
  platformBreakdown: {
    amazon: sortedProducts.filter(p => p.platform === 'amazon').length,
    ebay: sortedProducts.filter(p => p.platform === 'ebay').length
  }
};

const topProducts = sortedProducts.slice(0, 5);
let formattedResponse = '## üîç Multi-Platform Product Search: ' + originalQuery + '\n\n';
formattedResponse += '### üìä Search Overview\n';
formattedResponse += '- **Found:** ' + sortedProducts.length + ' products across Amazon & eBay\n';
formattedResponse += '- **Strategy:** ' + queryData.searchStrategy.replace('-', ' ') + '\n';
formattedResponse += '- **Price Range:** $' + stats.priceRange.min + ' - $' + stats.priceRange.max + '\n';
formattedResponse += '- **Average Price:** $' + stats.averagePrice.toFixed(2) + '\n\n';

formattedResponse += '### üèÜ Top Recommendations\n\n';

topProducts.forEach(product => {
  formattedResponse += '**' + product.rank + '. ' + product.title + '**\n';
  formattedResponse += '- üí∞ **Price:** ' + product.price + ' on ' + product.retailer + '\n';
  formattedResponse += '- üè™ **Platform:** ' + product.retailer + ' (' + product.affiliateEarnings + ')\n';
  formattedResponse += '- ‚≠ê **Rating:** ' + product.rating + '\n';
  formattedResponse += '- ‚ú® **Features:** ' + product.features.join(', ') + '\n';
  formattedResponse += '- üîó **Link:** ' + product.url + '\n';
  formattedResponse += '- üìù ' + product.description + '\n\n';
});

formattedResponse += '### üõí Platform Benefits\n\n';
formattedResponse += '**Amazon Advantages:**\n- ‚úÖ Prime shipping & returns\n- ‚úÖ New products with warranty\n- ‚úÖ Higher affiliate commissions (3-8%)\n\n';
formattedResponse += '**eBay Advantages:**\n- ‚úÖ Unique & vintage items\n- ‚úÖ Auction opportunities for deals\n- ‚úÖ Used items at lower prices\n- ‚úÖ Affiliate commissions (1-4%)\n\n';
formattedResponse += '### üí° Smart Shopping Tips\n- Compare prices across both platforms\n- Check seller ratings and return policies\n- Factor in shipping costs and delivery times\n- Look for coupon codes and promotions\n\n';
formattedResponse += '**Note:** This search combines the best of both worlds - Amazon\'s reliability and eBay\'s unique inventory. Full API integration coming soon for even better results!';

return {
  success: true,
  message: formattedResponse,
  products: sortedProducts,
  stats: stats,
  searchQuery: originalQuery,
  category: category,
  detectedBrand: detectedBrand,
  searchStrategy: queryData.searchStrategy,
  timestamp: new Date().toISOString(),
  integrationStatus: {
    amazonIntegrated: false,
    ebayIntegrated: false,
    usingMockData: true
  }
};
