// SIMPLE: Just pass the entire GPT content to main agent
console.log('=== SIMPLE CONTENT PASSTHROUGH ===');

try {
  const gptResponse = $('Fixed GPT-4o Processor').first().json;

  // Get the raw content using the correct path
  let content = null;
  if (gptResponse.choices && gptResponse.choices[0] && gptResponse.choices[0].message && gptResponse.choices[0].message.content) {
    content = gptResponse.choices[0].message.content;
    console.log('✅ Found content in choices[0].message.content');
  } else if (gptResponse.message?.content) {
    content = gptResponse.message.content;
    console.log('✅ Found content in message.content (fallback)');
  } else if (gptResponse.text) {
    content = gptResponse.text;
    console.log('✅ Found content in text field (fallback)');
  } else {
    console.error('❌ No content found');
    console.log('📊 Available structure:', Object.keys(gptResponse));
    content = 'No GPT response found';
  }

  console.log('📝 Content length:', content?.length || 0);
  console.log('📝 Content preview:', content?.substring(0, 200) || 'No content');

  // Just return the raw content - main agent will handle everything else
  return {
    output: content,
    success: true,
    timestamp: new Date().toISOString(),
    toolUsed: 'visual_search',
    responseSource: 'gpt_raw_content'
  };

} catch (error) {
  console.error('❌ Error:', error.message);
  return {
    output: 'Error accessing GPT response',
    success: false,
    timestamp: new Date().toISOString(),
    toolUsed: 'visual_search',
    responseSource: 'error'
  };
}

// FINAL: Universal Visual Search Input Processor with Complete Intelligence
const input = $input.first().json;
const sessionId = input.sessionId || `session_${Date.now()}`;

console.log('=== UNIVERSAL VISUAL SEARCH SYSTEM v1.0 ===');
console.log('📝 User Message:', input.chatInput?.substring(0, 100));
console.log('📁 Files Count:', input.files?.length || 0);
console.log('📍 User Location:', input.userLocation || 'Unknown');

// === LOCATION & REGION DETECTION ===
function detectUserRegion(location, timezone, language, ip) {
  const locationStr = (location || '').toLowerCase();
  const timezoneStr = (timezone || '').toLowerCase();
  const langStr = (language || '').toLowerCase();

  // Direct location matches
  if (locationStr.includes('united states') || locationStr.includes('usa') || locationStr.includes('us')) return 'United States';
  if (locationStr.includes('united kingdom') || locationStr.includes('uk') || locationStr.includes('england')) return 'United Kingdom';
  if (locationStr.includes('canada')) return 'Canada';
  if (locationStr.includes('australia')) return 'Australia';
  if (locationStr.includes('germany') || locationStr.includes('deutschland')) return 'Germany';
  if (locationStr.includes('france')) return 'France';
  if (locationStr.includes('italy')) return 'Italy';
  if (locationStr.includes('spain')) return 'Spain';
  if (locationStr.includes('netherlands')) return 'Netherlands';
  if (locationStr.includes('japan')) return 'Japan';
  if (locationStr.includes('south korea')) return 'South Korea';
  if (locationStr.includes('singapore')) return 'Singapore';
  if (locationStr.includes('india')) return 'India';

  // Timezone-based detection
  if (timezoneStr.includes('america/new_york') || timezoneStr.includes('america/chicago') || timezoneStr.includes('america/denver') || timezoneStr.includes('america/los_angeles')) return 'United States';
  if (timezoneStr.includes('europe/london')) return 'United Kingdom';
  if (timezoneStr.includes('america/toronto') || timezoneStr.includes('america/vancouver')) return 'Canada';
  if (timezoneStr.includes('australia/sydney') || timezoneStr.includes('australia/melbourne')) return 'Australia';
  if (timezoneStr.includes('europe/berlin')) return 'Germany';
  if (timezoneStr.includes('europe/paris')) return 'France';
  if (timezoneStr.includes('asia/tokyo')) return 'Japan';

  // Language-based fallback
  if (langStr === 'en-us') return 'United States';
  if (langStr === 'en-gb') return 'United Kingdom';
  if (langStr === 'en-ca') return 'Canada';
  if (langStr === 'en-au') return 'Australia';
  if (langStr === 'de') return 'Germany';
  if (langStr === 'fr') return 'France';

  return 'Global';
}

const detectedRegion = detectUserRegion(
  input.userLocation || input.location,
  input.timezone,
  input.language,
  input.userIP
);

// === RETAILER PREFERENCE DETECTION ===
const userMessage = (input.chatInput || '').toLowerCase();

const retailerPatterns = {
  'amazon': ['amazon', 'amazon.com', 'amazon.co.uk', 'amazon.de', 'amazon.ca'],
  'ebay': ['ebay', 'ebay.com'],
  'aliexpress': ['aliexpress', 'ali express'],
  'walmart': ['walmart', 'walmart.com'],
  'target': ['target', 'target.com'],
  'bestbuy': ['best buy', 'bestbuy'],
  'apple': ['apple', 'apple.com', 'apple store'],
  'nike': ['nike', 'nike.com'],
  'adidas': ['adidas', 'adidas.com'],
  'asos': ['asos', 'asos.com'],
  'zara': ['zara', 'zara.com'],
  'ikea': ['ikea', 'ikea.com'],
  'sephora': ['sephora', 'sephora.com'],
  'johnlewis': ['john lewis', 'johnlewis'],
  'currys': ['currys', 'currys.co.uk'],
  'mediamarkt': ['mediamarkt', 'media markt'],
  'zalando': ['zalando', 'zalando.com'],
  'aboutyou': ['aboutyou', 'about you']
};

// Detect user-specified retailers
const userSpecifiedRetailers = [];
Object.entries(retailerPatterns).forEach(([retailerKey, patterns]) => {
  patterns.forEach(pattern => {
    if (userMessage.includes(pattern)) {
      if (!userSpecifiedRetailers.includes(retailerKey)) {
        userSpecifiedRetailers.push(retailerKey);
      }
    }
  });
});

// Detect excluded retailers
const excludedRetailers = [];
const exclusionMatch = userMessage.match(/(?:not from|don't want|avoid|except|but not)\s+([a-z]+)/i);
if (exclusionMatch) {
  const excluded = exclusionMatch[1].toLowerCase();
  if (Object.keys(retailerPatterns).includes(excluded)) {
    excludedRetailers.push(excluded);
  }
}

// === GLOBAL RETAILER INTELLIGENCE ===
const globalRetailerTiers = {
  tier1_worldwide: {
    retailers: ['amazon', 'ebay', 'aliexpress'],
    description: 'Ships to 100+ countries',
    reliability: 'high'
  },
  tier2_major_global: {
    retailers: ['apple', 'nike', 'adidas', 'asos', 'zara'],
    description: 'Major global brands',
    reliability: 'high'
  },
  tier3_regional: {
    retailers: ['zalando', 'sephora', 'ikea'],
    description: 'Regional with some international shipping',
    reliability: 'medium'
  }
};

// === SMART RETAILER SELECTION ===
function selectOptimalRetailers(userRetailers, region, excluded) {
  let recommended = [];
  let strategy = 'global_fallback';

  if (userRetailers.length > 0) {
    strategy = 'user_specified';
    recommended = userRetailers.filter(r => !excluded.includes(r));
  } else if (region && region !== 'Global') {
    strategy = 'region_optimized';
    const regionMap = {
      'United States': ['amazon', 'target', 'walmart', 'bestbuy'],
      'United Kingdom': ['amazon', 'johnlewis', 'currys', 'asos'],
      'Germany': ['amazon', 'mediamarkt', 'zalando'],
      'Canada': ['amazon', 'bestbuy'],
      'Australia': ['amazon', 'jbhifi']
    };
    const localRetailers = regionMap[region] || [];
    recommended = [...localRetailers, ...globalRetailerTiers.tier1_worldwide.retailers]
      .filter((r, i, arr) => arr.indexOf(r) === i)
      .filter(r => !excluded.includes(r))
      .slice(0, 5);
  } else {
    strategy = 'global_fallback';
    recommended = [...globalRetailerTiers.tier1_worldwide.retailers, ...globalRetailerTiers.tier2_major_global.retailers.slice(0, 2)]
      .filter(r => !excluded.includes(r))
      .slice(0, 4);
  }

  return {
    retailers: recommended,
    strategy: strategy,
    needsUserInput: recommended.length < 2
  };
}

const retailerSelection = selectOptimalRetailers(userSpecifiedRetailers, detectedRegion, excludedRetailers);

// === IMAGE PROCESSING ===
let processedFiles = [];
if (input.files && input.files.length > 0) {
  processedFiles = input.files.map((file, index) => {
    console.log(`📄 Processing file ${index + 1}:`, {
      fileName: file.fileName,
      mimeType: file.mimeType,
      hasData: !!file.data
    });

    return {
      fileName: file.fileName || `image_${index + 1}`,
      mimeType: file.mimeType || 'image/jpeg',
      data: file.data,
      isImage: (file.mimeType || '').startsWith('image/')
    };
  }).filter(file => file.isImage);

  console.log(`✅ Processed ${processedFiles.length} image files`);
}

// === USER INTENT ANALYSIS ===
const intentAnalysis = {
  wantsColorChange: /different color|other color|another color|red|blue|green|pink|black|white/i.test(userMessage),
  wantsSizeChange: /different size|larger|smaller|bigger|size/i.test(userMessage),
  wantsAlternatives: /similar|alternative|like this|same style/i.test(userMessage),
  wantsSameProduct: /find this|where to buy|purchase|get this/i.test(userMessage),
  hasUrgency: /need|urgent|asap|quickly|soon/i.test(userMessage),
  hasBudget: /cheap|expensive|budget|price|cost/i.test(userMessage)
};

// === FINAL METADATA ASSEMBLY ===
const enhancedMetadata = {
  ...input,
  sessionId: sessionId,
  timestamp: new Date().toISOString(),

  // Image data
  processedFiles: processedFiles,
  hasImages: processedFiles.length > 0,

  // Location intelligence
  detectedRegion: detectedRegion,
  userLocation: input.userLocation || input.location,
  userTimezone: input.timezone,
  userLanguage: input.language,

  // Retailer intelligence
  userSpecifiedRetailers: userSpecifiedRetailers,
  excludedRetailers: excludedRetailers,
  recommendedRetailers: retailerSelection.retailers,
  retailerStrategy: retailerSelection.strategy,
  needsUserInput: retailerSelection.needsUserInput,

  // User intent
  intentAnalysis: intentAnalysis,

  // System intelligence
  globalTiers: globalRetailerTiers,

  // Smart hints for GPT
  smartHints: {
    hasSpecificRetailers: userSpecifiedRetailers.length > 0,
    hasLocation: detectedRegion !== 'Global',
    retailerCount: retailerSelection.retailers.length,
    needsMoreInfo: retailerSelection.needsUserInput,
    primaryIntent: Object.keys(intentAnalysis).find(key => intentAnalysis[key]) || 'general_search',
    globalAccessible: retailerSelection.retailers.some(r => globalRetailerTiers.tier1_worldwide.retailers.includes(r))
  }
};

console.log('🎯 Final metadata summary:', {
  region: detectedRegion,
  hasImages: enhancedMetadata.hasImages,
  retailerStrategy: retailerSelection.strategy,
  retailerCount: retailerSelection.retailers.length,
  needsInput: retailerSelection.needsUserInput
});

return enhancedMetadata;

