// Process incoming request from your app
const input = $input.first().json;

// Validate required fields
if (!input.userId) {
  throw new Error('userId is required');
}

if (!input.newsTopics || input.newsTopics.length === 0) {
  throw new Error('At least one news topic is required');
}

const userConfig = {
  userId: input.userId,
  newsTopics: Array.isArray(input.newsTopics) ? input.newsTopics : [input.newsTopics],
  preferredSources: input.preferredSources || [],
  timezone: input.timezone || 'UTC',
  language: input.language || 'en',
  region: input.region || 'global',
  maxArticlesPerTopic: Math.min(parseInt(input.maxArticlesPerTopic) || 3, 5), // Cap at 5
  excludeKeywords: input.excludeKeywords || []
};

console.log(`Processing news discovery for user: ${userConfig.userId}`);
console.log(`Topics: ${userConfig.newsTopics.join(', ')}`);
console.log(`Max articles per topic: ${userConfig.maxArticlesPerTopic}`);

// Create search tasks for each topic
const searchTasks = userConfig.newsTopics.map((topic, index) => {
  return {
    json: {
      userId: userConfig.userId,
      topic: topic.trim(),
      taskId: `topic_${index}`,
      userConfig: userConfig,
      searchIndex: index
    }
  };
});

return searchTasks;

// Enhanced Process AI Results with comprehensive debugging
console.log('üîç Starting AI Results Processing...');

// Step 1: Debug input data structure
console.log('üì• Input structure analysis:');
console.log('- $input.first():', JSON.stringify($input.first(), null, 2));
console.log('- Available keys in $input.first():', Object.keys($input.first()));

// Step 2: Try different ways to access AI response
let aiResponse = null;
const inputData = $input.first();

console.log('üîç Attempting to find AI response...');

// Try different possible locations for AI response
if (inputData.json) {
  console.log('- Checking inputData.json structure:', Object.keys(inputData.json));
  aiResponse = inputData.json.text || inputData.json.response || inputData.json.content || inputData.json.message;
}

if (!aiResponse) {
  aiResponse = inputData.text || inputData.response || inputData.content || inputData.message;
}

if (!aiResponse) {
  console.log('‚ùå Could not find AI response in any expected location');
  console.log('üìã Full input data:', JSON.stringify(inputData, null, 2));
  return [];
}

// Convert to string if needed and validate
if (typeof aiResponse !== 'string') {
  console.log('‚ö†Ô∏è AI response is not a string, type:', typeof aiResponse);
  console.log('üìã AI response value:', JSON.stringify(aiResponse, null, 2));

  if (aiResponse && typeof aiResponse === 'object') {
    // Try to extract text from object
    aiResponse = aiResponse.text || aiResponse.content || aiResponse.message || JSON.stringify(aiResponse);
  } else {
    aiResponse = String(aiResponse);
  }
}

console.log('‚úÖ Found AI response (type: ' + typeof aiResponse + '):', aiResponse.substring(0, 200) + '...');

// Step 3: Debug task data
console.log('üìã Task data analysis:');
let taskData = null;
try {
  taskData = $('Process App Request').first().json;
  console.log('‚úÖ Task data found:', {
    topic: taskData.topic,
    searchIndex: taskData.searchIndex,
    userConfig: taskData.userConfig ? 'present' : 'missing'
  });
} catch (error) {
  console.error('‚ùå Error accessing task data:', error.message);
  return [];
}

const userConfig = taskData.userConfig;
if (!userConfig) {
  console.error('‚ùå Missing userConfig in task data');
  return [];
}

const today = new Date().toLocaleDateString("en-CA", { timeZone: userConfig.timezone });
console.log('üìÖ Today date:', today);

// Step 4: Validate AI response
if (!aiResponse || (typeof aiResponse === 'string' && aiResponse.trim().length === 0)) {
  console.warn(`‚ùå Empty AI response for topic: ${taskData.topic}`);
  return [];
}

// Ensure aiResponse is a string for processing
if (typeof aiResponse !== 'string') {
  console.log('üîÑ Converting AI response to string for processing');
  aiResponse = String(aiResponse);
}

try {
  console.log('üßπ Cleaning AI response...');

  // Clean and parse AI response
  let cleanResponse = aiResponse.trim();
  console.log('Raw response length:', cleanResponse.length);

  // Remove markdown formatting
  cleanResponse = cleanResponse.replace(/```json|```/g, '').trim();

  // Extract JSON if wrapped in text
  const jsonMatch = cleanResponse.match(/\[.*\]/s);
  if (jsonMatch) {
    console.log('‚úÖ Found JSON array in response');
    cleanResponse = jsonMatch[0];
  } else {
    console.log('‚ö†Ô∏è No JSON array pattern found, using full response');
  }

  console.log('Cleaned response (first 300 chars):', cleanResponse.substring(0, 300));

  // Parse JSON
  let articles;
  try {
    articles = JSON.parse(cleanResponse);
    console.log('‚úÖ Successfully parsed JSON');
  } catch (parseError) {
    console.error('‚ùå JSON parsing failed:', parseError.message);
    console.log('üîç Attempting to fix common JSON issues...');

    // Try to fix common JSON issues
    let fixedResponse = cleanResponse
      .replace(/,\s*}/g, '}')  // Remove trailing commas
      .replace(/,\s*]/g, ']')  // Remove trailing commas in arrays
      .replace(/([{,]\s*)([a-zA-Z_][a-zA-Z0-9_]*)\s*:/g, '$1"$2":'); // Add quotes to keys

    try {
      articles = JSON.parse(fixedResponse);
      console.log('‚úÖ Successfully parsed fixed JSON');
    } catch (fixError) {
      console.error('‚ùå Even fixed JSON parsing failed:', fixError.message);
      return [];
    }
  }

  // Ensure array format
  if (!Array.isArray(articles)) {
    console.log('üîÑ Converting single article to array');
    articles = [articles];
  }

  console.log(`üìä Found ${articles.length} articles from AI`);

  // Debug first article structure
  if (articles.length > 0) {
    console.log('üîç First article structure:', JSON.stringify(articles[0], null, 2));
  }

  // Validate and process articles
  console.log('üîç Filtering articles...');
  const validArticles = articles.filter((article, index) => {
    const isValid = article && 
           article.title && 
           article.title.length > 5 && 
           article.summary &&
           article.source;

    if (!isValid) {
      console.log(`‚ùå Article ${index} failed validation:`, {
        hasArticle: !!article,
        hasTitle: !!(article && article.title),
        titleLength: article && article.title ? article.title.length : 0,
        hasSummary: !!(article && article.summary),
        hasSource: !!(article && article.source)
      });
    }

    return isValid;
  });

  console.log(`‚úÖ ${validArticles.length} articles passed validation`);

  const processedArticles = validArticles
    .slice(0, userConfig.maxArticlesPerTopic)
    .map((article, index) => {
      const guid = `ai_${userConfig.userId}_${taskData.searchIndex}_${index}_${Date.now()}`;

      const processedArticle = {
        date: today,
        topic: taskData.topic,
        title: article.title.substring(0, 500),
        snippet: article.summary.substring(0, 1000),
        contentLink: article.url || null,
        status: 'Not Sent',
        guid: guid,
        newsType: article.news_type || 'update',
        region: article.region || userConfig.region,
        relevanceScore: Math.max(1, Math.min(10, article.relevance_score || 5)),
        searchMethod: 'ai_curated',
        summary: article.source  // Maps to SUMMARY column in sheet
      };

      console.log(`‚úÖ Processed article ${index + 1}:`, {
        title: processedArticle.title.substring(0, 50) + '...',
        source: processedArticle.summary,
        guid: processedArticle.guid
      });

      return processedArticle;
    });

  console.log(`üéâ Successfully processed ${processedArticles.length} articles for: "${taskData.topic}"`);

  if (processedArticles.length > 0) {
    console.log('üìã Sample processed article:', JSON.stringify(processedArticles[0], null, 2));
  }

  return processedArticles;

} catch (error) {
  console.error(`‚ùå Error processing AI results for "${taskData.topic}":`, error.message);
  console.error('‚ùå Error stack:', error.stack);
  console.log('üìã Raw AI response (type: ' + typeof aiResponse + '):', 
    typeof aiResponse === 'string' ? aiResponse.substring(0, 500) : JSON.stringify(aiResponse, null, 2));

  // Return empty array to avoid breaking workflow
  return [];
}
