const timeZone = "Europe/Bucharest";

// Get today's date in the desired timezone (formatted as YYYY-MM-DD)
const now = new Date();
const today = now.toLocaleDateString("en-CA", { timeZone });

return items
  .map(item => {
    const postDate = new Date(item.json.pubDate);
    const postLocal = postDate.toLocaleDateString("en-CA", { timeZone });

    return {
      json: {
        guid: item.json.guid || item.json.link,
        title: item.json.title,
        link: item.json.link,
        date: postLocal,
        creator: item.json.creator || item.json["dc:creator"] || "unknown",
        snippet: item.json.contentSnippet || item.json.content || "(no caption)"
      }
    };
  })
  .filter(item => item.json.date === today);
