// Standardized input processing for all sub-workflows
// Use this code in the first Code node after the Execute Workflow Trigger

const input = $input.first().json;
let query = '';
let sessionId = null;
let userId = null;

// Handle multiple input formats from the main Travel Agent
if (input.query) {
  query = input.query;
} else if (input.chatInput) {
  query = input.chatInput;
} else if (input.input) {
  query = input.input;
} else if (input.text) {
  query = input.text;
} else if (input.message) {
  query = input.message;
} else if (typeof input === 'string') {
  query = input;
} else {
  // If it's a complex object, try to extract the user's request
  query = JSON.stringify(input);
}

// Extract metadata if available
sessionId = input.sessionId || input.session_id || null;
userId = input.userId || input.user_id || null;

// Set default dates for date-based workflows
const today = new Date();
const tomorrow = new Date(today);
tomorrow.setDate(tomorrow.getDate() + 1);
const nextWeek = new Date(today);
nextWeek.setDate(nextWeek.getDate() + 7);

return {
  originalQuery: query,
  processedQuery: query.toLowerCase(),
  sessionId: sessionId,
  userId: userId,
  timestamp: new Date().toISOString(),
  // Default dates (useful for accommodation/flight workflows)
  defaultCheckIn: today.toISOString().split('T')[0],
  defaultCheckOut: tomorrow.toISOString().split('T')[0],
  defaultDeparture: nextWeek.toISOString().split('T')[0],
  defaultReturn: new Date(nextWeek.getTime() + 7*24*60*60*1000).toISOString().split('T')[0],
  // Input validation
  hasQuery: query && query.trim().length > 0,
  rawInput: input
};

// Process Amadeus flight search results
const response = $input.first().json;
const searchData = $('Extract Flight Data').first().json.message.content;

if (!response || !response.data || response.data.length === 0) {
  return {
    error: true,
    message: `No flights found from ${searchData.origin} to ${searchData.destination} on ${searchData.departure_date}. Try different dates or nearby airports.`,
    searchData: searchData
  };
}

// Process flight offers
const flights = response.data.slice(0, 5).map((offer, index) => {
  const outbound = offer.itineraries[0];
  const segments = outbound.segments;
  const firstSegment = segments[0];
  const lastSegment = segments[segments.length - 1];

  // Calculate total duration
  const duration = outbound.duration.replace('PT', '').replace('H', 'h ').replace('M', 'm');

  // Get airline info
  const airline = firstSegment.carrierCode;

  // Get price
  const price = `${offer.price.total} ${offer.price.currency}`;

  // Check for stops
  const stops = segments.length - 1;
  const stopInfo = stops === 0 ? 'Direct' : `${stops} stop${stops > 1 ? 's' : ''}`;

  return {
    rank: index + 1,
    airline: airline,
    price: price,
    departure: {
      time: firstSegment.departure.at,
      airport: firstSegment.departure.iataCode
    },
    arrival: {
      time: lastSegment.arrival.at,
      airport: lastSegment.arrival.iataCode
    },
    duration: duration,
    stops: stopInfo,
    aircraft: firstSegment.aircraft ? firstSegment.aircraft.code : 'N/A',
    bookingToken: offer.id
  };
});

return {
  flights: flights,
  searchData: searchData,
  totalFound: response.data.length,
  success: true
};

