return {
  error: true,
  message: `I need both origin and destination to search for flights. You said: "${originalQuery}"\n\nPlease specify both locations, for example:\n• "Flight from New York to London"\n• "Flights JFK to CDG next Friday"\n• "Round trip Boston to Paris in July"`,
  suggestion: 'Please provide both departure and destination cities or airports.'
};

// Process Skyscanner results as fallback
const response = $input.first().json;
const searchData = $('Extract Flight Data').first().json.message.content;

if (!response || !response.Quotes || response.Quotes.length === 0) {
  return {
    error: true,
    message: `No flights found from ${searchData.origin} to ${searchData.destination}. Please check airport codes and try different dates.`,
    searchData: searchData
  };
}

// Process Skyscanner quotes (simplified)
const flights = response.Quotes.slice(0, 5).map((quote, index) => {
  const outbound = quote.OutboundLeg;
  const carrier = response.Carriers.find(c => c.CarrierId === outbound.CarrierIds[0]);

  return {
    rank: index + 1,
    airline: carrier ? carrier.Name : 'Unknown Airline',
    price: `$${quote.MinPrice}`,
    departure: {
      date: outbound.DepartureDate.split('T')[0],
      airport: searchData.origin
    },
    arrival: {
      airport: searchData.destination
    },
    direct: quote.Direct,
    bookingUrl: `https://www.skyscanner.com/transport/flights/${searchData.origin}/${searchData.destination}`
  };
});

return {
  flights: flights,
  searchData: searchData,
  totalFound: response.Quotes.length,
  success: true,
  source: 'skyscanner'
};

