// Handle location not found error
const locationError = $input.first().json;

return {
  error: true,
  message: locationError.message,
  suggestion: `Try searching for a major city near ${locationError.destination} or check the spelling.`
};

// Standardized input processing
const input = $input.first().json;
let query = '';
let sessionId = null;

if (input.query) {
  query = input.query;
} else if (input.chatInput) {
  query = input.chatInput;
} else if (input.input) {
  query = input.input;
} else if (typeof input === 'string') {
  query = input;
} else {
  query = JSON.stringify(input);
}

sessionId = input.sessionId || null;

const today = new Date();
const tomorrow = new Date(today);
tomorrow.setDate(tomorrow.getDate() + 1);

return {
  originalQuery: query,
  sessionId: sessionId,
  defaultCheckIn: today.toISOString().split('T')[0],
  defaultCheckOut: tomorrow.toISOString().split('T')[0],
  hasQuery: query && query.trim().length > 0
};

// Merge results from all three currency searches with error handling
let usdResults = null;
let eurResults = null;
let gbpResults = null;

try {
  usdResults = $('Search Hotels (USD)').first()?.json || null;
} catch (e) {
  console.log('USD search failed:', e.message);
}

try {
  eurResults = $('Search Hotels (EUR)').first()?.json || null;
} catch (e) {
  console.log('EUR search failed:', e.message);
}

try {
  gbpResults = $('Search Hotels (GBP)').first()?.json || null;
} catch (e) {
  console.log('GBP search failed:', e.message);
}

const locationData = $('Process Location1').first().json;

// Check if any results exist - updated for new API response structure
if ((!usdResults?.data?.hotels?.length) && (!eurResults?.data?.hotels?.length) && (!gbpResults?.data?.hotels?.length)) {
  return {
    error: true,
    message: `No hotels found in ${locationData.locationName} for the specified dates.`,
    suggestion: 'Try different dates or expand your search to nearby areas.'
  };
}

// Helper function to process hotel data with error handling - updated for new API structure
function processHotels(results, currency) {
  if (!results?.data?.hotels?.length) return [];

  return results.data.hotels.slice(0, 10).map(hotel => {
    try {
      // Updated field mapping for new API response structure
      const price = hotel.min_total_price || hotel.price_breakdown?.gross_price || 0;

      return {
        id: hotel.hotel_id,
        name: hotel.hotel_name || hotel.property?.name || 'Hotel name not available',
        price: price,
        currency: currency,
        rating: hotel.review_score || hotel.property?.review_score || 'No rating',
        reviewCount: hotel.review_nr || hotel.property?.review_nr || 0,
        distance: hotel.distance_to_cc ? `${hotel.distance_to_cc} km to center` : 'Distance not available',
        address: hotel.address || hotel.property?.address || 'Address not available',
        bookingUrl: hotel.url || '',
        imageUrl: hotel.main_photo_url || hotel.property?.main_photo_url || '',
        facilities: hotel.hotel_facilities?.slice(0, 3) || hotel.property?.facilities?.slice(0, 3) || [],
        isFreeCancellation: hotel.is_free_cancellable || hotel.property?.is_free_cancellable || false
      };
    } catch (e) {
      console.log(`Error processing hotel ${hotel.hotel_id}:`, e.message);
      return null;
    }
  }).filter(hotel => hotel !== null);
}

// Process all currency results
const usdHotels = processHotels(usdResults, 'USD');
const eurHotels = processHotels(eurResults, 'EUR');
const gbpHotels = processHotels(gbpResults, 'GBP');

// Create a map to merge hotels by ID
const hotelMap = new Map();

// Add USD prices
usdHotels.forEach(hotel => {
  if (!hotelMap.has(hotel.id)) {
    hotelMap.set(hotel.id, { ...hotel, prices: {} });
  }
  hotelMap.get(hotel.id).prices.USD = hotel.price;
});

// Add EUR prices
eurHotels.forEach(hotel => {
  if (!hotelMap.has(hotel.id)) {
    hotelMap.set(hotel.id, { ...hotel, prices: {} });
  }
  hotelMap.get(hotel.id).prices.EUR = hotel.price;
});

// Add GBP prices
gbpHotels.forEach(hotel => {
  if (!hotelMap.has(hotel.id)) {
    hotelMap.set(hotel.id, { ...hotel, prices: {} });
  }
  hotelMap.get(hotel.id).prices.GBP = hotel.price;
});

// Convert map to array and format prices
const mergedHotels = Array.from(hotelMap.values()).map((hotel, index) => ({
  rank: index + 1,
  name: hotel.name,
  prices: {
    USD: hotel.prices.USD ? `$${hotel.prices.USD}` : 'Not available',
    EUR: hotel.prices.EUR ? `â‚¬${hotel.prices.EUR}` : 'Not available',
    GBP: hotel.prices.GBP ? `Â£${hotel.prices.GBP}` : 'Not available'
  },
  rating: hotel.rating,
  reviewCount: hotel.reviewCount,
  distance: hotel.distance,
  address: hotel.address,
  bookingUrl: hotel.bookingUrl,
  imageUrl: hotel.imageUrl,
  facilities: hotel.facilities,
  isFreeCancellation: hotel.isFreeCancellation
}));

// Sort by rating (highest first)
mergedHotels.sort((a, b) => {
  if (typeof a.rating === 'number' && typeof b.rating === 'number') {
    return b.rating - a.rating;
  }
  return 0;
});

// Track which currencies returned results
const availableCurrencies = [];
if (usdHotels.length > 0) availableCurrencies.push('USD');
if (eurHotels.length > 0) availableCurrencies.push('EUR');
if (gbpHotels.length > 0) availableCurrencies.push('GBP');

return {
  hotels: mergedHotels.slice(0, 8),
  location: locationData.locationName,
  searchData: locationData.searchData,
  totalFound: mergedHotels.length,
  currencies: availableCurrencies,
  searchCriteria: {
    destination: locationData.searchData.destination,
    checkIn: locationData.searchData.check_in,
    checkOut: locationData.searchData.check_out,
    adults: locationData.searchData.adults,
    rooms: locationData.searchData.rooms
  },
  success: true,
  apiStatus: {
    usd: usdResults ? 'success' : 'failed',
    eur: eurResults ? 'success' : 'failed',
    gbp: gbpResults ? 'success' : 'failed'
  },
  debug: {
    usdCount: usdHotels.length,
    eurCount: eurHotels.length,
    gbpCount: gbpHotels.length
  }
};

const response = $input.first().json;
const searchData = $('Extract Search Data1').first().json.message.content;

// Handle API errors
if (response.error) {
  return {
    error: true,
    message: `API Error: ${response.error.message || 'Unknown error occurred'}`
  };
}

// Check if response has data array
if (!response || !response.data || !Array.isArray(response.data) || response.data.length === 0) {
  return {
    error: true,
    message: `Location not found: ${searchData.destination}`,
    destination: searchData.destination
  };
}

// Find the best location match
// Priority: city > region > airport > hotel
const locations = response.data;
let selectedLocation = null;

// First try to find a city
const cityLocation = locations.find(loc => loc.search_type === 'city');
if (cityLocation) {
  selectedLocation = cityLocation;
} else {
  // If no city, try region
  const regionLocation = locations.find(loc => loc.search_type === 'region');
  if (regionLocation) {
    selectedLocation = regionLocation;
  } else {
    // Fall back to first available location
    selectedLocation = locations[0];
  }
}

if (!selectedLocation) {
  return {
    error: true,
    message: `No suitable location found for: ${searchData.destination}`,
    destination: searchData.destination
  };
}

return {
  locationId: selectedLocation.dest_id,
  locationType: selectedLocation.dest_type,
  locationName: selectedLocation.label,
  searchData: searchData,
  success: true,
  locationDetails: {
    name: selectedLocation.name,
    country: selectedLocation.country,
    region: selectedLocation.region,
    searchType: selectedLocation.search_type,
    hotelCount: selectedLocation.nr_hotels
  }
};

const originalQuery = $('Process Input').first().json.originalQuery;
return {
  error: true,
  message: `Please specify a destination. You said: "${originalQuery}"`,
  suggestion: 'Please provide a city or location name for your hotel search.'
};
